mapper.select:
select client_id, client_secret
from oauth_client_details
where client_id = #{username}

mapper.insert:
insert into nagato.nagato_plain_user(user_no_generate, user_nick)
values(#{user.uuid}, #{user.username});

insert into nagato.nagato_user_password(user_no, user_password)
values(
          (
              select user_no from nagato.nagato_plain_user
              where user_no_generate = #{user.uuid}
          ),
          #{user.password}
      )
